import React, { useEffect, useState, Component } from "react";
import "./Main.css";
import { booleanTypeAnnotation } from "@babel/types";

export default function Main() {
  let [operacao, setOperacao] = useState("");
  let [historico, setHistorico] = useState("");

  class Botao extends Component {
    render() {
      return (
        <button
          className="botao"
          value={this.props.valor}
          onClick={e => adicionaValor(e)}
        >
          {this.props.valor}
        </button>
      );
    }
  }

  function adicionaValor(event) {
    setOperacao(operacao + event.target.value);
    setHistorico(historico + event.target.value);
  }

  function zeraValor() {
    setOperacao("");
    setHistorico("");
  }

  function fazCalculo() {
    setOperacao(eval(operacao));
    setHistorico(historico + `=${eval(operacao)}`);
  }

  return (
    <div className="tela">
      <div className="container">
        <div className="resultado">{operacao}</div>
        <div className="operacoes">{historico}</div>
        <div>
          <Botao valor="7"></Botao>
        </div>
        <div>
          <Botao valor="8"></Botao>
        </div>
        <div>
          <Botao valor="9"></Botao>
        </div>
        <div>
          <Botao valor="+"></Botao>
        </div>
        <div>
          <Botao valor="4"></Botao>
        </div>
        <div>
          <Botao valor="5"></Botao>
        </div>
        <div>
          <Botao valor="6"></Botao>
        </div>
        <div>
          <Botao valor="-"></Botao>
        </div>
        <div>
          <Botao valor="1"></Botao>
        </div>
        <div>
          <Botao valor="2"></Botao>
        </div>
        <div>
          <Botao valor="3"></Botao>
        </div>
        <div>
          <Botao valor="*"></Botao>
        </div>
        <div>
          <Botao valor="C"></Botao>
        </div>
        <div>
          <Botao valor="0"></Botao>
        </div>
        <div>
          <button className="botao" onClick={() => fazCalculo()}>
            {" "}
            =
          </button>
        </div>
        <div>
          <Botao valor="/"></Botao>
        </div>
      </div>
    </div>
  );
}
