import React, { useEffect, useState, Component } from "react";
import "./Main.css";
import { booleanTypeAnnotation } from "@babel/types";

export default function Main() {
  let [operacao, setOperacao] = useState("");
  let [historico, setHistorico] = useState("");

  class Botao extends Component {
    render() {
      return (
        <button
          className="botao this.props.styleName"
          value={this.props.valor}
          onClick={e => adicionaValor(e)}
        >
          {this.props.valor}
        </button>
      );
    }
  }

  function adicionaValor(event) {
    setOperacao(operacao + event.target.value);
    setHistorico(historico + event.target.value);
  }

  function zeraValor() {
    setOperacao("");
    setHistorico("");
  }

  function fazCalculo() {
    setOperacao(eval(operacao));
    setHistorico(historico + `=${eval(operacao)}`);
  }

  return (
    <div className="tela">
      <div className="container">
        <div className="resultado">{operacao}</div>
        <div className="operacoes">{historico}</div>

        <Botao valor="e"></Botao>
        <Botao valor="pi"></Botao>
        <Botao valor="sin"></Botao>
        <Botao valor="deg"></Botao>
        <button className="botao" onClick={() => zeraValor()}>
          C
        </button>
        <Botao valor="("></Botao>
        <Botao valor=")"></Botao>
        <Botao valor="/"></Botao>

        <Botao valor="7"></Botao>
        <Botao valor="8"></Botao>
        <Botao valor="9"></Botao>
        <Botao valor="*"></Botao>

        <Botao valor="4"></Botao>
        <Botao valor="5"></Botao>
        <Botao valor="6"></Botao>
        <Botao valor="-"></Botao>

        <Botao valor="1"></Botao>
        <Botao valor="2"></Botao>
        <Botao valor="3"></Botao>
        <Botao valor="+"></Botao>

        <Botao className="zero" valor="0"></Botao>

        <Botao valor="."></Botao>
        <button className="botao" onClick={() => fazCalculo()}>
          =
        </button>
      </div>
    </div>
  );
}
